mapper.select:
select blog_id blogId ,blog_title blogTitle,blog_content blogContent ,user_id userId from t_blog

where


    blog_id =#{id1}



    blog_id =#{id2}



or


    blog_id =#{id1}



    blog_id =#{id2}

mapper.update:
update t_blog


        blog_title=#{blogTitle},


        blog_content=#{blogContent},


        user_id  = #{userId}


where
blog_id =#{blogId}

update t_user


        user_name=#{userName},


        user_pwd=#{userPwd}


where
user_id =#{userId}
